Code Example:
// Use high contrast colors
<p className="text-slate-900 dark:text-slate-100">
  {/* 4.5:1 contrast on white/dark backgrounds */}
</p>
// Check with Tailwind config
colors: {
  primary: { DEFAULT: '#1d4ed8' } // Ensure AA contrast
}

// Ensure all interactive elements are focusable
<button onClick={handleClick}>
  {t('button.submit')}
</button>
// Custom components need tabIndex
<div role="button" tabIndex={0} onKeyDown={handleKeyDown}>
  {t('card.clickable')}
</div>

// Tailwind focus styles
<button className="focus:ring-2 focus:ring-blue-500 focus:outline-none">
  {t('button.action')}
</button>
// Global focus styles
@layer base {
  :focus-visible {
    @apply ring-2 ring-blue-500 ring-offset-2;
  }
}

// Explicit association
<label htmlFor="email">{t('form.email')}</label>
<input id="email" type="email" />
// Or implicit
<label>
  {t('form.email')}
  <input type="email" />
</label>

// Accessible error
<input
  aria-invalid={!!error}
  aria-describedby="email-error"
/>
{error && (
  <p id="email-error" role="alert">
    {t('errors.invalidEmail')}
  </p>
)}

// layout.tsx
export default function RootLayout({
  children,
  params: { locale }
}: {
  children: React.ReactNode;
  params: { locale: string };
}) {
  return (
    <html lang={locale}>
      {children}
    </html>
  );
}

// Tailwind motion-safe
<div className="transition-transform motion-safe:hover:scale-105">
// CSS media query
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    transition-duration: 0.01ms !important;
  }
}
